#endif // SNAKE_H
```
**snake.c:**
```c
#include "snake.h"
#include <SDL2/SDL.h>
#include <SDL2/SDL_image.h>
#include <stdio.h>
#include <stdlib.h>
#include <time.h>

// Global variables
SDL_Window* window;
SDL_Renderer* renderer;
SDL_Texture* snake_texture;
SDL_Texture* food_texture;
int score;
int snake_direction;
int snake_x, snake_y;
int snake_length;
int grid[GRID_SIZE][GRID_SIZE];

void init_snake() {
    // Initialize SDL
    SDL_Init(SDL_INIT_VIDEO);
    window = SDL_CreateWindow("Snake Game", SDL_WINDOWPOS_CENTERED, SDL_WINDOWPOS_CENTERED, 640, 480, SDL_WINDOW_SHOWN);
    renderer = SDL_CreateRenderer(window, -1, SDL_RENDERER_ACCELERATED);

    // Load images
    snake_texture = IMG_LoadTexture(renderer, "snake.png");
    food_texture = IMG_LoadTexture(renderer, "food.png");

    // Initialize game state
    score = 0;
    snake_direction = 0; // 0: up, 1: right, 2: down, 3: left
    snake_x = GRID_SIZE / 2;
    snake_y = GRID_SIZE / 2;
    snake_length = 1;
    for (int i = 0; i < GRID_SIZE; i++) {
        for (int j = 0; j < GRID_SIZE; j++) {
            grid[i][j] = 0;
        }
    }
}

void update_snake() {
    // Update snake position
    switch (snake_direction) {
        case 0: // up
            snake_y -= 1;
            break;
        case 1: // right
            snake_x += 1;
            break;
        case 2: // down
            snake_y += 1;
            break;
        case 3: // left
            snake_x -= 1;
            break;
    }

    // Check for collisions with walls and self
    if (snake_x < 0 || snake_x >= GRID_SIZE || snake_y < 0 || snake_y >= GRID_SIZE || grid[snake_x][snake_y] == 1) {
        // Game over
        printf("Game Over!\n");
        exit(0);
    }

    // Check for food
    if (grid[snake_x][snake_y] == 2) {
        // Eat food, increase score and snake length
        score += 1;
        snake_length += 1;
        grid[snake_x][snake_y] = 0;
    }
}

void render_snake() {
    // Clear screen
    SDL_SetRenderDrawColor(renderer, 0, 0, 0, 255);
    SDL_RenderClear(renderer);

    // Draw snake
    for (int i = 0; i < snake_length; i++) {
        SDL_Rect rect = {snake_x * SNAKE_IMAGE_WIDTH, snake_y * SNAKE_IMAGE_HEIGHT, SNAKE_IMAGE_WIDTH, SNAKE_IMAGE_HEIGHT};
        SDL_RenderCopy(renderer, snake_texture, NULL, &rect);
    }

    // Draw food
    SDL_Rect food_rect = {rand() % GRID_SIZE * SNAKE_IMAGE_WIDTH, rand() % GRID_SIZE * SNAKE_IMAGE_HEIGHT, SNAKE_IMAGE_WIDTH, SNAKE_IMAGE_HEIGHT};
    SDL_RenderCopy(renderer, food_texture, NULL, &food_rect);

    // Draw score
    SDL_SetRenderDrawColor(renderer, 255, 255, 255, 255);
    SDL_RenderDrawLine(renderer, 0, 0, 640, 20);
    SDL_RenderDrawLine(renderer, 0, 20, 640, 20);
    SDL_RenderDrawLine(renderer, 0, 20, 0, 20);
    SDL_RenderDrawLine(renderer, 640, 20, 640, 20);
    SDL_RenderDrawText(renderer, "Score: %d", score, 10, 10);

    // Update screen
    SDL_RenderPresent(renderer);
}

void handle_events() {
    SDL_Event event;
    while (SDL_PollEvent(&event)) {
        switch (event.type) {
            case SDL_QUIT:
                // Quit game
                exit(0);
                break;
            case SDL_KEYDOWN:
                switch (event.key.keysym.sym) {
                    case SDLK_UP:
                        snake_direction = 0;
                        break;
                    case SDLK_RIGHT:
                        snake_direction = 1;
                        break;
                    case SDLK_DOWN:
                        snake_direction = 2;
                        break;
                    case SDLK_LEFT:
                        snake_direction = 3;
                        break;
                }
                break;
        }
    }
}

void load_images() {
    // Load snake and food images
    // ...
}

void free_resources() {
    // Free resources
    SDL_DestroyTexture(snake_texture);
    SDL_DestroyTexture(food_texture);
    SDL_DestroyRenderer(renderer);
    SDL_DestroyWindow(window);
    SDL_Quit();
}

int main() {
    init_snake();
    while (1) {
        update_snake();
        handle_events();
        render_snake();
        SDL_Delay(1000 / SNAKE_SPEED);
    }
    free_resources();
    return 0;
}
