          <!-- chat history messages will be displayed here -->
        </ul>
      </div>
      <div class="modal-footer">
        <button type="button" class="btn btn-secondary" data-dismiss="modal">Close</button>
      </div>
    </div>
  </div>
</div>

<!-- The chat input and button -->
<div class="chat-input-container">
  <input id="chat-input" type="text" placeholder="Type a message...">
  <button id="send-button" class="btn btn-primary" data-toggle="modal" data-target="#chat-modal">Send</button>
</div>

<!-- JavaScript code to populate the chat history -->
<script>
  const chatLog = document.getElementById('chat-log');
  const chatInput = document.getElementById('chat-input');
  const sendButton = document.getElementById('send-button');

  // Initialize chat history with 4 messages
  const chatHistory = [
    {
      type: 'human',
      message: 'Hey, how are you?',
      timestamp: '12:34 PM',
    },
    {
      type: 'bot',
      message: 'I\'m doing well, thanks for asking!',
      timestamp: '12:35 PM',
    },
    {
      type: 'human',
      message: 'What\'s the weather like today?',
      timestamp: '12:36 PM',
    },
    {
      type: 'bot',
      message: 'It\'s partly cloudy with a high of 75°F. Enjoy the day!',
      timestamp: '12:37 PM',
    },
  ];

  // Function to update chat history messages
  function updateChatLog() {
    chatLog.innerHTML = '';
    chatHistory.forEach((message) => {
      const listItem = document.createElement('li');
      listItem.classList.add('list-group-item');
      listItem.innerHTML = `
        <span class="text-muted">${message.timestamp}</span>
        <span class="chat-message ${message.type === 'human' ? 'text-left' : 'text-right'}">
          ${message.type === 'human' ? 'You: ' : 'Bot: '} ${message.message}
