      chatLog.appendChild(listItem);
    });
  }

  // Update chat history when send button is clicked
  sendButton.addEventListener('click', () => {
    const userMessage = chatInput.value.trim();
    if (userMessage !== '') {
      chatHistory.unshift({
        type: 'human',
        message: userMessage,
        timestamp: new Date().toLocaleTimeString(),
      });
      updateChatLog();
      chatInput.value = '';
    }
  });

  // Update chat history when the page loads
  updateChatLog();
</script>

