int countWordOccurrences(const string& filename, const string& targetWord) {
    ifstream file(filename);
    if (!file.is_open()) {
        cerr << "Error opening file: " << filename << endl;
        return 0;
    }

    map<string, int> wordCount;
    string word;

    while (file >> word) {
        if (word == targetWord) {
            wordCount[targetWord]++;
        }
    }

