        // Update game state
        update_game();

        // Render game
        render_game();

        // Update screen
        SDL_RenderPresent(renderer);

        // Cap frame rate
        SDL_Delay(1000 / SNAKE_SPEED);
    }

    // Clean up
    SDL_DestroyRenderer(renderer);
    SDL_DestroyWindow(window);
    SDL_Quit();
    return 0;
}
