// Import necessary dependencies
import { addAlias } from '@cypress/logger';
import docker from 'dockerode';
import mkdirp from 'mkdirp';
import fs from 'fs';

// Set up Docker container
const dockerode = new docker({
  socketPath: '/var/run/docker.sock',
  host: 'localhost',
  port: 2375,
  protocol: 'unix'
});

const containerName = 'my-test-container';
const buildArgs = {
  NODE_ENV: 'test'
};

// Function to create a Docker container
async function createContainer() {
  return new Promise((resolve, reject) => {
    dockerode.createContainer({
      image: 'my-test-image',
      Name: containerName,
      Env: Object.keys(buildArgs).map((key) => `${key}=${buildArgs[key]}`)
    }, (err, container) => {
      if (err) {
        reject(err);
      } else {
        resolve(container);
      }
    });
  });
}

// Function to start a Docker container
async function startContainer(container: any) {
  return new Promise((resolve, reject) => {
    container.start({ detach: true }, (err, data) => {
      if (err) {
        reject(err);
      } else {
        resolve(data);
      }
    });
  });
}

// Function to stop a Docker container
async function stopContainer(container: any) {
  return new Promise((resolve, reject) => {
    container.stop({ timeout: 10 }, (err, data) => {
      if (err) {
        reject(err);
      } else {
        resolve(data);
      }
    });
  });
}

// Function to clean up Docker containers
async function tearDown() {
  return new Promise(async (resolve, reject) => {
    try {
      const containers = await dockerode.listContainers({ filters: { status: 'running' } });
      if (containers.length > 0) {
        for (const container of containers) {
          await stopContainer(dockerode.getContainer(container.getId()));
          await dockerode.removeContainer(container.getId(), { force: true });
        }
      }
      resolve();
    } catch (err) {
      reject(err);
    }
  });
}

// Create a custom command to start the Docker container before each test
Cypress.Commands.add('startDocker', async () => {
  await createContainer();
  await startContainer(await createContainer());
});

// Create a custom command to stop the Docker container after each test
Cypress.Commands.add('stopDocker', async () => {
  await stopContainer(await dockerode.getContainer(containerName));
});

// Set up screenshot functionality to capture and save screenshots
Cypress.on('after:run', () => {
  mkdirp.sync('screenshots');
});

Cypress.on('after:task', (taskResult) => {
  const screenshotPath = `screenshots/${taskResult.test.description}_fail-${taskResult.screenShotPath}`;
  fs.renameSync(taskResult.screenShotPath, screenshotPath);
});

// Add alias to log level
addAlias({
  level: 'debug',
  prefix: '[Cypress]',
  colors: true
});
