int countWord(const string& filename, const string& targetWord) {
    int count = 0;
    ifstream file(filename);
    if (!file.is_open()) {
        cout << "Error: Unable to open file";
        return -1;
    }

    string line;
    while (getline(file, line)) {
        istringstream iss(line);
        string word;
        while (iss >> word) {
            if (word == targetWord) {
