#include <string>
#include <unordered_map>
#include <iostream>

std::string markdownToHtml(const std::string& markdown) {
    std::string html;
    std::unordered_map<std::string, std::string> markdownToHtmlMapping = {
        // Headers
        {"#", "<h1>"},
        {"##", "<h2>"},
        {"###", "<h3>"},
        // Bold text
        {"**", "<b>"},
        {"__", "<b>"},
        // Italic text
        {"*", "<i>"},
        {"_", "<i>"},
        // Links
        {"[url]", "<a href='url'>"},
        // Lists
        {"* ", "<ul><li>"},
        {"1. ", "<ol><li>"},
        // Images
        {"![image]", "<img src='image' alt=''/>"},
    };

    for (char& c : markdown) {
        bool inCodeBlock = false;
        for (const auto& mapping : markdownToHtmlMapping) {
            size_t pos = markdown.find(mapping.first);
            while (pos != std::string::npos) {
                if (!inCodeBlock) {
                    html += mapping.second;
                }
                markdown.replace(pos, mapping.first.size(), "");
                pos = markdown.find(mapping.first, pos);
            }
        }
    }

    // Remove leading/trailing whitespace
    html.erase(0, html.find_first_not_of(' '));
    html.erase(html.find_last_not_of(' ') + 1);

    return html;
}
```
