    if (food == NULL) {
        printf("Failed to load food image: %s\n", SDL_GetError());
        exit(1);
    }
}

// Function to render the game screen
void render_screen() {
    // Clear the screen
    SDL ẩmSurface *screen = SDL_SetVideoMode(SCREEN_WIDTH, SCREEN_HEIGHT, 32);
    SDL_FillRect(screen, NULL, WHITE);

    // Draw snake body
    for (int i = 0; i < snake_len; i++) {
        SDL_Rect snake_rect = {snake_x[i] * BLOCK_SIZE, snake_y[i] * BLOCK_SIZE, BLOCK_SIZE, BLOCK_SIZE};
        SDL_BlitSurface(snake_body, NULL, screen, &snake_rect);
    }

    // Draw food
    SDL_Rect food_rect = {food_x * BLOCK_SIZE, food_y * BLOCK_SIZE, BLOCK_SIZE, BLOCK_SIZE};
    SDL_BlitSurface(food, NULL, screen, &food_rect);

    // Update the screen
    SDL_UpdateWindowSurface(screen);
}

// Function to handle user input
void handle_input() {
    // Get user input
