    while (SDL_PollEvent(&event)) {
        if (event.type == SDL_QUIT) {
            game_over = 1;
        } else if (event.type == SDL_KEYDOWN) {
            switch (event.key.keysym.sym) {
                case SDLK_UP:
                    direction = 1;
                    break;
                case SDLK_DOWN:
                    direction = 2;
                    break;
                case SDLK_LEFT:
                    direction = 3;
                    break;
                case SDLK_RIGHT:
                    direction = 4;
                    break;
            }
        }
    }
