// Function to update game logic
void update_game() {
    // Move snake
    for (int i = snake_len - 1; i > 0; i--) {
        snake_x[i] = snake_x[i - 1];
        snake_y[i] = snake_y[i - 1];
    }

    // Update snake head position
    switch (direction) {
        case 1:
            snake_y[0]--;
            break;
        case 2:
            snake_y[0]++;
            break;
        case 3:
            snake_x[0]--;
            break;
        case 4:
            snake_x[0]++;
            break;
