\documentclass{article}
\usepackage{nicefrac}
\usepackage{grid}

\newcommand{\chessboard}[1]{%
  \begin{center}
  \begin{grid}(8,8)
  \foreach \x in {1,...,8} {
    \foreach \y in {1,...,8} {
      \ifthenelse{\x==4 && \y==4}{\cellcolor{white}}{\cellcolor{gray}}
      \node at (\x-\x/2pt, \y-\y/2pt) {\ifthenelse{\x<4 || \y<4}{\ifthenelse{\x<2 || \y<2}{}{\rotatebox{90}{\tiny\white}\hspace{2pt}}}{\ifthenelse{\x<2 || \y<2}{}{\rotatebox{90}{\tiny\black}}}}
    }
  \end{grid}
  \end{center}
}

