    markdown.replace("]", "\">");
    // Remove newline characters
    boost::ptr_container<std::string> lines;
    boost::split(lines, markdown, boost::is_any_of("\n"));
    std::string html = "";
    for (auto line : lines) {
        html += line + "<br>";
    }
    return html;
}
