  for (const num of arr) {
    if (isPrime(num)) {
      console.log(`Found prime number: ${num}`);
      return;
    }
  }
}

function isPrime(n) {
  if (n <= 1) return false;
  for (let i = 2, sqrt = Math.sqrt(n); i <= sqrt; i++) {
    if (n % i === 0) return false;
  }
  return true;
}

