#include <emarked/renderer.h>
#include <string>

std::string markdownToHtml(const std::string& markdown) {
    emarked::Renderer renderer;
    emarked::MarkdownString md(markdown);
    emarked::HtmlString html;
    renderer.render(md, &html);
    return html.GetString();
}
