using namespace std;

int countWordOccurrences(const string& filename, const string& word) {
    ifstream file(filename);
    if (!file.is_open()) {
        cout << "Error opening file: " << filename << endl;
        return -1;
    }

    string line;
    int count = 0;
   istringstream iss;

    while (getline(file, line)) {
        iss.str(line);
        while (iss >> word) {
