  // Convert Markdown italic text to HTML italic text
  html += boost::replace_all_copy(markdown, "*", "<i>");
  markdown = boost::replace_all_copy(markdown, "*", "</i>");
  html += boost::replace_all_copy(markdown, "_", "<i>");
  markdown = boost::replace_all_copy(markdown, "_", "</i>");

  // Convert Markdown bold text to HTML bold text
  html += boost::replace_all_copy(markdown, "**", "<b>");
  markdown = boost::replace_all_copy(markdown, "**", "</b>");

  // Convert Markdown links to HTML links
  html += boost::replace_all_copy(markdown, "[(.*)]", "<a href=\"\\1\">");

  // Convert Markdown images to HTML images
  html += boost::replace_all_copy(markdown, "!\\[(.*)\\]", "<img src=\"\\1\">");

  // Convert Markdown code blocks to HTML code blocks
  html += boost::replace_all_copy(markdown, "```", "<pre><code>");
  markdown = boost::replace_all_copy(markdown, "```", "</code></pre>");

  return html + markdown;
}
