import data.sigma.basic

def timSort [α : Type] [decidable_eq α] (xs : list α) : list α :=
let rec merge<> : list α → list α → list α :=
  fun xs ys =>
    if xs = [] then ys else
      if ys = [] then xs else
        let x ← xs.head,
          let xs_tail := xs.tail,
          let y ← ys.head,
          let ys_tail := ys.tail,
        if x ≤ y then x :: (merge xs_tail ys) else y :: (merge ys_tail xs)
