class Stack:
    def __init__(self):
        self.stack = []

    def push(self, x):
        self.stack.append(x)

    def pop(self):
        if len(self.stack) < 1:
            return None
        return self.stack.pop()

    def size(self):
        return len(self.stack)


class QueueUsingStacks:
    def __init__(self):
        self.stack1 = Stack()
        self.stack2 = Stack()

    def enqueue(self, item):
        while self.stack1.size() > 0:
            temp = self.stack1.pop()
            self.stack2.push(temp)
        self.stack1.push(item)
        while self.stack2.size() > 0:
            temp = self.stack2.pop()
            self.stack1.push(temp)

    def dequeue(self):
        if self.stack1.size() < 1:
            return None
        return self.stack1.pop()

    def size(self):
        return self.stack1.size()


