  const command = clientcmds.get(interaction.commandName);

  if (!command) {
    console.error(`No command matching "${interaction.commandName}" was found.`);
    return;
  }

  try {
    await command.execute(interaction);
  } catch (error) {
    console.error(`Error executing command: ${error}`);
    await interaction.reply({ ephemeral: true, content: `Error: ${error}` });
  }
});

class BanCommand extends Command {
  constructor() {
    super({
      name: 'ban',
      description: 'Bans a user from the guild',
      options: [
        {
          type: 'user',
          name: 'user',
          description: 'The user to ban',
          required: true
        }
      ]
    });
  }

  async execute(interaction) {
    const user = interaction.options.getUser('user');
    if (!user) {
      return interaction.reply({ ephemeral: true, content: 'Invalid user' });
    }

    if (!interaction.guild.members.me.permissions.has(PermissionFlagsBits.BanMembers)) {
      return interaction.reply({ ephemeral: true, content: 'I don\'t have the necessary permissions to ban users' });
    }

    if (!interaction.guild.members.cache.get(user.id)) {
      return interaction.reply({ ephemeral: true, content: 'User not found in this guild' });
    }

    try {
      await interaction.guild.members.ban(user);
      return interaction.reply({ ephemeral: true, content: `User banned: ${user.tag}` });
    } catch (error) {
      return interaction.reply({ ephemeral: true, content: `Error: ${error}` });
    }
  }
}

client.commands.set('ban', new BanCommand());

client.login('YOUR_BOT_TOKEN');
