```
const { Client, GatewayIntentBits } = require('discord.js');
const client = new Client({ intents: [GatewayIntentBits.Guilds, GatewayIntentBits.GuildMembers] });

client.commands = new Collection();

client.commands.register('ban', {
  name: 'ban',
  description: 'Bans a user from the server',
  options: [
    {
      name: 'user',
      description: 'The user to ban',
      type: 'user',
      required: true,
    },
  ],
  async execute(interaction) {
    const user = interaction.options.getUser('user');
    const reason = interaction.options.getString('reason');

    if (!interaction.member.permissions.has('BAN_MEMBERS')) {
      return interaction.reply({ content: 'You do not have permission to ban members.', ephemeral: true });
    }

    if (!user) {
      return interaction.reply({ content: 'You must specify a user to ban.', ephemeral: true });
    }

    try {
      await interaction.guild.members.ban(user, { reason: reason || 'No reason provided' });
      return interaction.reply({ content: ` Successfully banned ${user.username} from the server.`, ephemeral: true });
    } catch (error) {
      return interaction.reply({ content: 'Failed to ban user: ' + error.message, ephemeral: true });
    }
  },
});

client.on('ready', () => {
  console.log('The bot is online!');
});

client.on('interactionCreate', async (interaction) => {
  if (!interaction.isCommand()) return;

  const command = client.commands.get(interaction.commandName);
  if (!command) return;

  try {
    await command.execute(interaction);
  } catch (error) {
    console.error(error);
    await interaction.reply({ content: 'There was an error while executing this command.', ephemeral: true });
  }
});

client.login('YOUR_BOT_TOKEN');
```
