```cpp
#include <iostream>
#include <fstream>
#include <sstream>
#include <string>

using namespace std;

int countWordOccurrences(string filename, string word) {
    int count = 0;
    ifstream file(filename);

    if (file.is_open()) {
        string line;
        while (getline(file, line)) {
            stringstream ss(line);
            string temp;
            while (getline(ss, temp, ' ')) {
                if (temp == word) {
                    count++;
                }
            }
        }
        file.close();
    } else {
        cout << "Unable to open file";
        return -1;
    }
    return count;
}

int main() {
    string filename, word;
    cout << "Enter the filename: ";
    cin >> filename;
    cout << "Enter the word to count: ";
    cin >> word;

    int occurrences = countWordOccurrences(filename, word);

    if (occurrences != -1) {
        cout << "The word '" << word << "' occurs " << occurrences << " times in the file." << endl;
    }

    return 0;
}
