```javascript
const { Client, GatewayIntentBits } = require('discord.js');
const client = new Client({
  intents: [
    GatewayIntentBits.Guilds,
    GatewayIntentBits.GuildMembers,
    GatewayIntentBits.GuildBans,
  ],
});

client.commands = new Map();

client.commands.set('ban', {
  name: 'ban',
  description: 'Ban a user',
  options: [
    {
      name: 'user',
      description: 'The user to ban',
      type: 'user',
      required: true,
    },
    {
      name: 'reason',
      description: 'The reason for the ban',
      type: 'string',
      required: false,
    },
  ],
  execute(interaction) {
    const user = interaction.options.getUser('user');
    const reason = interaction.options.getString('reason');
    const guild = interaction.guild;

    if (!guild.members.me.permissions.has('BAN_MEMBERS'))
      return interaction.reply('I don\'t have the permission to ban users.');

    if (!user)
      return interaction.reply('Please provide a valid user mention.');

    guild.members.ban(user, { reason: reason });

    interaction.reply(`Banned ${user.username} with the reason ${reason}`);
  },
});

client.once('ready', () => {
  console.log('Ready!');
  client.application.commands.set(client.commands);
});

client.on('interactionCreate', async (interaction) => {
  if (!interaction.isCommand()) return;

  const command = client.commands.get(interaction.commandName);
  if (!command) return;

  try {
    await command.execute(interaction);
  } catch (error) {
    console.error(error);
    interaction.reply('Error executing command!');
  }
});
```

