std::string markdownToHtml(const std::string& markdown) {
  std::ostringstream html;
  html << "<html><body>";
  for (const auto& line : std::regex_split(markdown, "\n")) {
    // Convert headers
    std::smatch match;
    if (std::regex_match(line, match, std::regex("^#+(.*)$"))) {
      html << "<h" << match.str(1).size() << "'>" << match.str(1) << "</h" << match.str(1).size() << ">";
    }
    // Convert bold text
    else if (std::regex_match(line, match, std::regex("^\\*(.*)\\*$"))) {
      html << "<b>" << match.str(1) << "</b>";
    }
    // Convert italic text
    else if (std::regex_match(line, match, std::regex("^\\*(.*)$"))) {
      html << "<i>" << match.str(1) << "</i>";
    }
    // Convert links
    else if (std::regex_match(line, match, std::regex("^(.*)\\[(.+?)\\]\\((.+?)\\)$"))) {
      html << "<a href=\"" << match.str(3) << "\">" << match.str(1) << " [" << match.str(2) << "]</a>";
    }
    // Convert images
    else if (std::regex_match(line, match, std::regex("^(.*)\\[(.+?)\\]\\((.+?)\\вада(.*)$"))) {
      html << "<img src=\"" << match.str(3) << "\" alt=\"" << match.str(1) << "\">";
    }
    // Convert paragraphs
    else {
      html << "<p>" << line << "</p>";
    }
  }
  html << "</body></html>";
  return html.str();
}
```
