#include <SDL2/SDL.h>

SDL_Surface* load_image(const char* filename) {
    SDL_Surface* image = IMG_Load(filename);
    if (image == NULL) {
        printf("Failed to load image: %s\n", IMG_GetError());
        return NULL;
    }
    return image;
}

int get_image_width(SDL_Surface* image) {
    return image->w;
}

