    return image->h;
}
```
**Step 3: Create the Snake Game**

Create a new file `snake.c` with the following content:
```c
#include "snake.h"
#include "grid.h"
#include "images.h"

// Define the snake's initial position and direction
int snake_x = 10;
int snake_y = 10;
int snake_dir_x = 1;
int snake_dir_y = 0;

// Define the snake's body parts
struct SnakeBody {
    SDL_Rect rect;
    struct SnakeBody* next;
};

// Create a new snake body part
struct SnakeBody* create_snake_body(int x, int y) {
    struct SnakeBody* body = (struct SnakeBody*)malloc(sizeof(struct SnakeBody));
    body->rect.x = x;
    body->rect.y = y;
    body->rect.w = 10; // Assuming 10x10 pixels
    body->rect.h = 10;
    body->next = NULL;
    return body;
}

// Update the snake's position and direction
void update_snake(struct SnakeBody* body) {
    // Update the snake's position based on its direction
    snake_x += snake_dir_x;
    snake_y += snake_dir_y;

    // Clear the previous snake body parts
    struct SnakeBody* prev = body;
    while (prev != NULL) {
        prev->rect.x = 0;
        prev->rect.y = 0;
        prev = prev->next;
    }

    // Create new snake body parts at the updated position
    body = create_snake_body(snake_x, snake_y);
    body->next = prev;
}
```
**Step 4: Render the Game**

Create a new file `grid.c` with the following content:
```c
#include "grid.h"
#include "images.h"

// Define the grid's dimensions
int grid_width = 20;
int grid_height = 20;

// Define the grid's cells
struct GridCell {
    SDL_Rect rect;
};

// Create a new grid cell
struct GridCell* create_grid_cell(int x, int y) {
    struct GridCell* cell = (struct GridCell*)malloc(sizeof(struct GridCell));
    cell->rect.x = x;
    cell->rect.y = y;
    cell->rect.w = 20; // Assuming 20x20 pixels
    cell->rect.h = 20;
    return cell;
}

// Render the grid
void render_grid(SDL_Renderer* renderer) {
    for (int i = 0; i < grid_width; i++) {
        for (int j = 0; j < grid_height; j++) {
            // Draw the grid cell
            SDL_Rect rect = {i * 20, j * 20, 20, 20};
            SDL_SetRenderDrawColor(renderer, 255, 255, 255, 255);
            SDL_RenderFillRect(renderer, &rect);
        }
    }
}
```
**Step 5: Create the Main Game Loop**

Create a new file `main.c` with the following content:
```c
#include "main.h"
#include "snake.h"
#include "grid.h"

int main(int argc, char** argv) {
    // Initialize SDL and create a window
    if (sdl_init() != 0) {
        return 1;
    }

    // Load the snake's image
    SDL_Surface* snake_image = load_image("snake.png");
    if (snake_image == NULL) {
        return 1;
    }

    // Load the food's image
    SDL_Surface* food_image = load_image("food.png");
    if (food_image == NULL) {
        return 1;
    }

    // Create the snake's body
    struct SnakeBody* snake_body = create_snake_body(snake_x, snake_y);

    // Main game loop
    while (true) {
        // Handle events
        SDL_Event event;
        while (SDL_PollEvent(&event)) {
            if (event.type == SDL_QUIT) {
                return 0;
            } else if (event.type == SDL_KEYDOWN) {
                // Handle key pressed
                if (event.key.keysym.sym == SDLK_LEFT) {
                    snake_dir_x = -1;
                    snake_dir_y = 0;
                } else if (event.key.keysym.sym == SDLK_RIGHT) {
                    snake_dir_x = 1;
                    snake_dir_y = 0;
                } else if (event.key.keysym.sym == SDLK_UP) {
                    snake_dir_x = 0;
                    snake_dir_y = -1;
                } else if (event.key.keysym.sym == SDLK_DOWN) {
                    snake_dir_x = 0;
                    snake_dir_y = 1;
                }
            }
        }

        // Update the snake's position and direction
        update_snake(snake_body);

        // Render the game
        SDL_RenderClear(renderer);
        render_grid(renderer);
        for (struct SnakeBody* body = snake_body; body != NULL; body = body->next) {
            SDL_Rect rect = {body->rect.x, body->rect.y, 10, 10};
            SDL_RenderCopy(renderer, snake_image, NULL, &rect);
        }
        SDL_RenderPresent(renderer);
    }

    return 0;
}
```
**Step 6: Put it all Together**

