Here's a high-level example of how you can structure your code:
```c
#include <SDL2/SDL.h>
#include <stdio.h>
#include <stdlib.h>

// Define the game constants
#define WIDTH 640
#define HEIGHT 480
#define FPS 60

// Load the images
SDL_Surface* snakeImage = NULL;
SDL_Surface* foodImage = NULL;
SDL_Surface* backgroundImage = NULL;

// Define the snake struct
typedef struct {
    int x, y; // position
    int dx, dy; // direction
    int length; // number of segments
    int* segments; // array of segment positions
} Snake;

// Initialize the game
void initGame() {
    // Initialize SDL
    SDL_Init(SDL_INIT_VIDEO);
    // Create the window
    SDL_Window* window = SDL_CreateWindow("Snake Game", SDL_WINDOWPOS_CENTERED, SDL_WINDOWPOS_CENTERED, WIDTH, HEIGHT, SDL_WINDOW_SHOWN);
    // Create the renderer
    SDL_Renderer* renderer = SDL_CreateRenderer(window, -1, SDL_RENDERER_ACCELERATED);

    // Load the images
    snakeImage = SDL_LoadBMP("snake.bmp");
    foodImage = SDL_LoadBMP("food.bmp");
    backgroundImage = SDL_LoadBMP("background.bmp");

    // Initialize the snake
    Snake snake;
    snake.x = 10;
    snake.y = 10;
    snake.dx = 0;
    snake.dy = 0;
    snake.length = 1;
    snake.segments = (int*)malloc(snake.length * sizeof(int));
    snake.segments[0] = 0;
}

// Main game loop
int main() {
    // Initialize the game
    initGame();

    // Main loop
    while (1) {
        // Handle input
        SDL_Event event;
        while (SDL_PollEvent(&event)) {
            if (event.type == SDL_KEYDOWN) {
                if (event.key.keysym.sym == SDLK_LEFT) {
                    snake.dx = -1;
                    snake.dy = 0;
                } else if (event.key.keysym.sym == SDLK_RIGHT) {
                    snake.dx = 1;
                    snake.dy = 0;
                } else if (event.key.keysym.sym == SDLK_UP) {
                    snake.dx = 0;
                    snake.dy = -1;
                } else if (event.key.keysym.sym == SDLK_DOWN) {
                    snake.dx = 0;
                    snake.dy = 1;
                }
            }
        }

        // Update the game state
        updateGameState(&snake);

        // Render the game
        SDL_RenderClear(renderer);
        SDL_RenderCopy(renderer, backgroundImage, NULL, NULL);
        for (int i = 0; i < snake.length; i++) {
            SDL_Rect rect = { snake.segments[i] * 10, 0, 10, 10 };
            SDL_RenderCopy(renderer, snakeImage, NULL, &rect);
        }
        SDL_Rect foodRect = { snake.foodX * 10, snake.foodY * 10, 10, 10 };
        SDL_RenderCopy(renderer, foodImage, NULL, &foodRect);
        SDL_RenderPresent(renderer);

        // Delay to control the framerate
        SDL_Delay(1000 / FPS);
    }

    // Clean up
    SDL_Quit();
    free(snake.segments);
    return 0;
}

// Update the game state
void updateGameState(Snake* snake) {
    // Move the snake
    for (int i = snake.length - 1; i > 0; i--) {
        snake.segments[i] = snake.segments[i - 1];
    }
    snake.segments[0] = (snake.x + snake.dx) * 10;
    snake.segments[0] += (snake.y + snake.dy) * 10;

    // Check for collisions with the wall
    if (snake.x + snake.dx < 0 || snake.x + snake.dx >= WIDTH / 10) {
        snake.dx = 0;
        snake.dy = 0;
    }
    if (snake.y + snake.dy < 0 || snake.y + snake.dy >= HEIGHT / 10) {
        snake.dx = 0;
        snake.dy = 0;
    }

    // Check for collisions with the food
    if (snake.x + snake.dx * 10 == snake.foodX && snake.y + snake.dy * 10 == snake.foodY) {
        // Eat the food, grow the snake
        snake.length++;
        // Update the food position
        snake.foodX = (rand() % (WIDTH / 10)) * 10;
        snake.foodY = (rand() % (HEIGHT / 10)) * 10;
    }
}

// Free the game resources
void freeGame() {
    // Free the images
    SDL_FreeSurface(snakeImage);
    SDL_FreeSurface(foodImage);
    SDL_FreeSurface(backgroundImage);
    // Free the snake struct
    free(snake.segments);
}
```
