const { Client, GatewayIntentBits, SlashCommandBuilder } = require('discord.js');
const client = new Client({
  intents: [
    GatewayIntentBits.Guilds,
    GatewayIntentBits.GuildMembers,
    GatewayIntentBits.GuildBans,
  ],
});

client.commands = new Map();

const banCommand = new SlashCommandBuilder()
  .setName('ban')
  .setDescription('Bans a user from the server.')
  .addUserOption((option) =>
    option.setName('user').setDescription('The user to ban.')
  );

client.commands.set('ban', banCommand);

client.on('interactionCreate', async (interaction) => {
  if (!interaction.isCommand()) return;

  const { command, options } = interaction;

  if (command.name === 'ban') {
    const user = options.getUser('user');
    if (!user) {
      await interaction.reply({
        content: 'You must specify a user to ban.',
        ephemeral: true,
      });
      return;
    }

    const member = await interaction.guild.members.fetch(user.id);
    if (!member) {
      await interaction.reply({
        content: `Could not find member ${user.username}.`,
        ephemeral: true,
      });
      return;
    }

    try {
      await interaction.guild.members.ban(member, { reason: 'You were banned' });
      await interaction.reply({
        content: `Successfully banned ${user.username}.`,
      });
    } catch (error) {
      console.error(error);
      await interaction.reply({
        content: 'Failed to ban the user.',
        ephemeral: true,
      });
    }
  }
});

client.login('YOUR_BOT_TOKEN');
