```cpp
#include <iostream>
#include <fstream>
#include <string>
#include <map>
#include <cctype>

using namespace std;

int countWordOccurrences(const string& filename, const string& word) {
    ifstream fileReadStream(filename);
    map<string, int> wordCount;

    if (fileReadStream.is_open()) {
        string line;
        while (getline(fileReadStream, line)) {
            for (auto& c : line) {
                c = tolower(c);  // Convert to lowercase for case-insensitive matching
            }

            size_t pos = line.find(word);
            while (pos != string::npos) {
                wordCount[word]++;
                pos = line.find(word, pos + 1);
            }
        }

        fileReadStream.close();

        if (wordCount.find(word) != wordCount.end()) {
            return wordCount[word];
        } else {
            return 0;
        }
    } else {
        cerr << "Unable to open file " << filename << endl;
        return -1;
    }
}

int main() {
    string filename, word;

    cout << "Enter the filename: ";
    cin >> filename;
    cout << "Enter the word to count: ";
    cin >> word;

    int occurrences = countWordOccurrences(filename, word);

    if (occurrences >= 0) {
        cout << "The word \"";
        cout << word;
