Here's a basic structure to get you started:
```c
#include <SDL2/SDL.h>
#include <stdbool.h>

// Game constants
#define SCREEN_WIDTH 640
#define SCREEN_HEIGHT 480
#define FRAME_RATE 60

// Game structure
typedef struct {
    // Game state
    bool running;
    bool paused;
    int score;
    // Snake structure
    int x, y, direction;
    struct {
        int x, y;
    } *body;
    int body_length;
} Game;

// Function prototypes
void init_game(Game *game);
void update_game(Game *game);
void render_game(Game *game);
void handle_input(Game *game, SDL_Event *event);
void free_game(Game *game);

int main(int argc, char *argv[]) {
    // Initialize SDL
    SDL_Init(SDL_INIT_VIDEO | SDL_INIT_INPUT);
    // Create a window
    SDL_Window *window = SDL_CreateWindow("Snake Game", SDL_WINDOWPOS_CENTERED, SDL_WINDOWPOS_CENTERED, SCREEN_WIDTH, SCREEN_HEIGHT, SDL_WINDOW_SHOWN);
    // Create a renderer
    SDL_Renderer *renderer = SDL_CreateRenderer(window, -1, SDL_RENDERER_ACCELERATED);
    // Initialize game
    Game game;
    init_game(&game);
    // Main loop
    while (game.running) {
        // Handle input
        SDL_Event event;
        while (SDL_PollEvent(&event)) {
            handle_input(&game, &event);
        }
        // Update game state
        update_game(&game);
        // Render game
        render_game(&game);
        // Cap at 60 FPS
        SDL_Delay(1000 / FRAME_RATE);
    }
    // Free game and cleanup
    free_game(&game);
    SDL_DestroyRenderer(renderer);
    SDL_DestroyWindow(window);
    SDL_Quit();
    return 0;
}

// Initialize game
void init_game(Game *game) {
    // Initialize game state
    game->running = true;
    game->paused = false;
    game->score = 0;
    // Initialize snake
    game->x = SCREEN_WIDTH / 2;
    game->y = SCREEN_HEIGHT / 2;
    game->direction = 0; // 0 - up, 1 - right, 2 - down, 3 - left
    game->body = (struct {
        int x, y;
    } *)malloc(10 * sizeof(struct {
        int x, y;
    }));
    game->body_length = 10;
}

// Update game state
void update_game(Game *game) {
    // Update snake movement
    if (game->direction == 0) {
        game->y -= 10;
    } else if (game->direction == 1) {
        game->x += 10;
    } else if (game->direction == 2) {
        game->y += 10;
    } else if (game->direction == 3) {
        game->x -= 10;
    }
    // Check for collisions
