}

// Render game
void render_game(Game *game) {
    // Clear screen
    SDL_SetRenderDrawColor(renderer, 0, 0, 0, 255);
    SDL_RenderClear(renderer);
    // Draw background
    SDL_Texture *background = SDL_CreateTextureFromSurface(renderer, LoadImage("background.png"));
    SDL_RenderCopy(renderer, background, NULL, NULL);
    SDL_DestroyTexture(background);
    // Draw snake
    for (int i = 0; i < game->body_length; i++) {
        int x = game->body[i].x;
        int y = game->body[i].y;
        SDL_Texture *snake = SDL_CreateTextureFromSurface(renderer, LoadImage("snake.png"));
        SDL_RenderCopy(renderer, snake, NULL, &(SDL_Rect) { x, y, 10, 10 });
        SDL_DestroyTexture(snake);
    }
    // Draw food
    // ...
    // Update screen
    SDL_RenderPresent(renderer);
}

// Handle input
void handle_input(Game *game, SDL_Event *event) {
    if (event->type == SDL_KEYDOWN) {
        // Handle keyboard input
        if (event->key.keysym.sym == SDLK_UP) {
            game->direction = 0;
        } else if (event->key.keysym.sym == SDLK_RIGHT) {
            game->direction = 1;
        } else if (event->key.keysym.sym == SDLK_DOWN) {
            game->direction = 2;
        } else if (event->key.keysym.sym == SDLK_LEFT) {
            game->direction = 3;
        }
    }
}

// Free game
void free_game(Game *game) {
    free(game->body);
}
