```cpp
#include <iostream>
#include <fstream>
#include <string>
#include <map>

using namespace std;

int countWordOccurrence(string filename, string targetWord) {
    map<string, int> wordCount;
    int lineNum = 0;

    ifstream file(filename);

    if (!file) {
        cout << "Error opening file";
        return -1;
    }

    string line, word;

    while (getline(file, line)) {
        lineNum++;
        size_t pos = 0;
        string::size_type prevPos = 0, currPos = 0;

        while ((currPos = line.find_first_of(" ,.\t", prevPos)) != string::npos) {
            if (currPos != pos) {
                word = line.substr(pos, currPos - pos);
                prevPos = currPos + 1;

                if (word.size() >= 2 && word[0] >= 'a' && word[0] <= 'z') {
                    transform(word.begin(), word.end(), word.begin(), ::tolower);

                    if (word == targetWord)
                        wordCount[targetWord]++;
                }

                pos = currPos + 1;
            } else {
                pos = currPos;
            }
        }

        if (pos < line.size()) {
            word = line.substr(pos);
            if (word.size() >= 2 && word[0] >= 'a' && word[0] <= 'z') {
                transform(word.begin(), word.end(), word.begin(), ::tolower);

                if (word == targetWord)
                    wordCount[targetWord]++;
            }
        }
    }

    if (!wordCount.empty())
        cout << "Word '" << targetWord << "' occurred " << wordCount[targetWord] << " times in " << filename;

    return lineNum;
}

int main() {
    string filename = "test.txt";
    string targetWord = "example";

    countWordOccurrence(filename, targetWord);

    return 0;
}
