const { Client, GatewayIntentBits } = require('discord.js');
const client = new Client({
  intents: [
    GatewayIntentBits.Guilds,
    GatewayIntentBits.GuildMembers,
    GatewayIntentBits.GuildMessages,
  ],
});

client.ws.version = 10;

const token = 'YOUR_BOT_TOKEN_HERE'; // Replace with your bot token

client.commands = new Map();

client.commands.set('ban', {
  name: 'ban',
  description: 'Bans a user from the server',
  options: [
    {
      name: 'user',
      description: 'The user to ban',
      type: 'user',
      required: true,
    },
    {
      name: 'reason',
      description: 'The reason for the ban',
      type: 'string',
      required: false,
    },
  ],
  execute: async (interaction) => {
    if (!interaction.member.permissions.has('BAN_MEMBERS')) {
      return interaction.reply({ content: 'You do not have the `BAN_MEMBERS` permission.', ephemeral: true });
    }

    const user = interaction.options.getUser('user');
    const reason = interaction.options.getString('reason') || 'No reason provided';

    await interaction.guild.members.ban(user, { reason });

    return interaction.reply({
      content: `${user.tag} has been banned.`,
      ephemeral: true,
    });
  },
});

client.on('ready', () => {
  console.log(`Logged in as ${client.user.tag}!`);
});

client.on('interactionCreate', async (interaction) => {
  if (!interaction.isCommand()) return;

  const command = client.commands.get(interaction.commandName);

  if (!command) return;

  try {
    await command.execute(interaction);
  } catch (error) {
    console.error(error);
    await interaction.reply({ content: 'There was an error while executing this command!', ephemeral: true });
  }
});

client.login(token);
```

