  for (let i = 0; i < arr.length; i++) {
    let num = arr[i];
    if (isPrime(num)) {
      console.log(num);
    }
  }
}

function isPrime(n) {
  if (n <= 1) return false;
  for (let i = 2, sqrt = Math.sqrt(n); i <= sqrt; i++) {
    if (n % i === 0) return false;
  }
  return true;
}

